interface Car {
    image:String | null;
    company:String| null;
    model:String| null;
}
const FormView: React.FC = (

) =>{

    var carsInSale:Car[] 

    var IsCarsInsale:any= localStorage.getItem('cars')

     if(IsCarsInsale !==null ){

        carsInSale = JSON.parse(IsCarsInsale)
        console.log(carsInSale)

    }


    const saveCar =()=>{

        const newImage:any = document.querySelector("#image")
        const newCompany:any = document.querySelector("#company")
        const newModel:any = document.querySelector("#model")
        const newCar:Car ={
            image:newImage,
            company:newCompany,
            model:newModel
        }
        if(carsInSale !==null){
            console.log(IsCarsInsale)
        }else{
            console.log(JSON.stringify({ image:newImage,
                company:newCompany,
                model:newModel}))
           localStorage.setItem("cars",JSON.stringify(newCar))
        }

    }



    return(
        <form>
            <label htmlFor='company' >Company:</label>
            <input id='company'></input>
            <label htmlFor='model'>Model:</label>
            <input id='model'></input>
            <label htmlFor='image'>Select a pic:</label>
            <input id="image"></input>
            <button type="button" onClick={saveCar}>Enviar</button>
        </form>
    )
}

export default FormView
